    QBCore.Functions.CreateUseableItem("ciatracker", function(source)
        truckenb = source
        local src = source
       
        local Player = QBCore.Functions.GetPlayer(src)
        if Player.PlayerData.job.name == "police"  then
        TriggerClientEvent("opentrcermenu", src)
        else
            TriggerClientEvent('QBCore:Notify', src, 'police only can use this item', 'error')
        end
    
    end)
    RegisterNetEvent('opentrac')
    AddEventHandler('opentrac', function(truckernub)
        local src = source
        local Player = QBCore.Functions.GetPlayer(src)
    
        if Player.PlayerData.job.name == "police" then
            if truckernub.phonetrar then
                local phoneNumber = truckernub.phonetrar
                local Target = QBCore.Functions.GetPlayerByPhone(phoneNumber)
                if Target then
                    if Target.PlayerData.metadata["tracker"] then
                        local needItem = Target.Functions.GetItemByName('phone')
                        if needItem then
                            TriggerClientEvent("fbi23:client:SendTrackerLocation", Target.PlayerData.source, src)
                            TriggerClientEvent('QBCore:Notify', src, 'Start hack.', 'error')
                        else
                            TriggerClientEvent('QBCore:Notify', src, 'Player does not have a phone.', 'error')
                        end
                    else
                        TriggerClientEvent('QBCore:Notify', src, 'This person is not wearing an anklet.', 'error')
                    end
                else
                    TriggerClientEvent('QBCore:Notify', src, 'No player found with the provided phone number.', 'error')
                end
            else
                TriggerClientEvent('QBCore:Notify', src, 'Please provide a phone number.', 'error')
            end
        else
            TriggerClientEvent('QBCore:Notify', src, 'This command can only be used by on-duty police officers.', 'error')
        end
    end)

RegisterNetEvent('opentrac')
AddEventHandler('opentrac', function(truckernub)
    local src = source
    local Player = QBCore.Functions.GetPlayer(src)

    if Player.PlayerData.job.name == "police" then
        if truckernub.phonetrar then
            local phoneNumber = truckernub.phonetrar
            local Target = QBCore.Functions.GetPlayerByPhone(phoneNumber)
            if Target then
                if Target.PlayerData.metadata["tracker"] then
                    local needItem = Target.Functions.GetItemByName('phone')
                    if needItem then
                        TriggerClientEvent("Dc:client:SendTrackerLocation", Target.PlayerData.source, src)
                        TriggerClientEvent('QBCore:Notify', src, 'Start hack.', 'error')
                    else
                        TriggerClientEvent('QBCore:Notify', src, 'Player does not have a phone.', 'error')
                    end
                else
                    TriggerClientEvent('QBCore:Notify', src, 'This person is not wearing an anklet.', 'error')
                end
            else
                TriggerClientEvent('QBCore:Notify', src, 'No player found with the provided phone number.', 'error')
            end
        else
            TriggerClientEvent('QBCore:Notify', src, 'Please provide a phone number.', 'error')
        end
    else
        TriggerClientEvent('QBCore:Notify', src, 'This command can only be used by on-duty police officers.', 'error')
    end
end)
RegisterNetEvent('canceltrc', function(truckernub)
    local src = source
    local Player = QBCore.Functions.GetPlayer(src)
    if Player.PlayerData.job.name == "police"  then
        if truckernub.phonetrar then
            local phoneNumber = truckernub.phonetrar
            local Target = QBCore.Functions.GetPlayerByPhone(phoneNumber)
            if Target then
                if Target.PlayerData.metadata["tracker"] then
                    TriggerClientEvent("fbi:client:SendTrackerLocation", Target.PlayerData.source, src)
                else
                    TriggerClientEvent('QBCore:Notify', src, 'This person does not have an anklet', 'error')
                end
            end
        end
    else
        TriggerClientEvent('QBCore:Notify', src, 'For on-duty police officers only', 'error')
    end
    end)




