    local src = source
    local Player = QBCore.Functions.GetPlayer(src)
    local ped = GetPlayerPed(src)
    local coords = GetEntityCoords(ped)
        TriggerClientEvent('Dc:client:truckenabt', truckenb, coords, src)

end)

RegisterNetEvent("Dc:server:ggad1", function()
    local src = source
    local Player = QBCore.Functions.GetPlayer(src)
    local ped = GetPlayerPed(src)
    local coords = GetEntityCoords(ped)

        TriggerClientEvent('Dc:client:testruckenabt111', src, coords, src)
end)
